interface FoodItem {
  id: string;
  name: string;
  type: string;
  origin: string;
  rating: number;
  description: string;
  imageUrl: string;
  mostIconic?: string;
}

const SupabaseData = () => {
  const queryClient = useQueryClient();
  const [isSeedingDummyData, setIsSeedingDummyData] = React.useState(false);
  const [isDeletingAllData, setIsDeletingAllData] = React.useState(false);

  // Fetch food items from Supabase
  const {
    data: foods,
    isLoading,
    isError,
    error,
  } = useQuery<FoodItem[], Error>({
    queryKey: ["supabaseFoods"],
    queryFn: async () => {
      const { data, error } = await supabase.from("food_items").select("*");
      if (error) throw error;
      return data as FoodItem[];
    },
  });

  // Mutation to add a new food item
  const addFoodMutation = useMutation<
    FoodItem,
    Error,
    Omit<FoodItem, "id">
  >({
    mutationFn: async (newFood) => {
      const { data, error } = await supabase
        .from("food_items")
        .insert([newFood])
        .select()
        .single();
      if (error) throw error;
      return data as FoodItem;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["supabaseFoods"] });
      showSuccess("Item makanan berhasil ditambahkan!");
      setNewFood({
        name: "",
        type: "",
        origin: "",
        rating: 0,
        description: "",
        imageUrl: "",
        mostIconic: "",
      });
    },
    onError: (err) => {
      showError(`Gagal menambahkan item makanan: ${err.message}`);
    },
  });

  // Mutation to delete a single food item
  const deleteFoodItemMutation = useMutation<void, Error, string>({
    mutationFn: async (idToDelete) => {
      const { error } = await supabase.from("food_items").delete().eq("id", idToDelete);
      if (error) throw error;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["supabaseFoods"] });
      showSuccess("Item makanan berhasil dihapus!");
    },
    onError: (err) => {
      showError(`Gagal menghapus item makanan: ${err.message}`);
    },
  });

  // Mutation to delete all food items
  const deleteAllFoodItemsMutation = useMutation<void, Error>({
    mutationFn: async () => {
      // Menggunakan UUID 'nil' yang valid dalam klausa WHERE untuk menghapus semua baris
      const { error } = await supabase.from("food_items").delete().neq("id", "00000000-0000-0000-0000-000000000000"); 
      if (error) throw error;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["supabaseFoods"] });
      showSuccess("Semua item makanan berhasil dihapus!");
    },
    onError: (err) => {
      showError(`Gagal menghapus semua item makanan: ${err.message}`);
    },
    onSettled: () => {
      setIsDeletingAllData(false);
    }
  });

  const [newFood, setNewFood] = React.useState<Omit<FoodItem, "id">>({
    name: "",
    type: "",
    origin: "",
    rating: 0,
    description: "",
    imageUrl: "",
    mostIconic: "",
  });

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>,
  ) => {
    const { name, value } = e.target;
    setNewFood((prev) => ({
      ...prev,
      [name]: name === "rating" ? parseFloat(value) : value,
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    addFoodMutation.mutate(newFood);
  };

  const handleSeedDummyData = async () => {
    setIsSeedingDummyData(true);
    try {
      await seedSupabaseWithDummyData();
      queryClient.invalidateQueries({ queryKey: ["supabaseFoods"] });
      showSuccess("Data dummy berhasil ditambahkan ke Supabase!");
    } catch (err: any) {
      showError(`Gagal menambahkan data dummy: ${err.message}`);
    } finally {
      setIsSeedingDummyData(false);
    }
  };

  const handleDeleteAllData = async () => {
    if (window.confirm("Apakah Anda yakin ingin menghapus SEMUA data makanan dari Supabase? Tindakan ini tidak dapat dibatalkan.")) {
      setIsDeletingAllData(true);
      deleteAllFoodItemsMutation.mutate();
    }
  };

  const handleDeleteSingleFood = (id: string, name: string) => {
    if (window.confirm(`Apakah Anda yakin ingin menghapus "${name}"?`)) {
      deleteFoodItemMutation.mutate(id);
    }
  };

  const isAnySeedingOrDeletingActive = isSeedingDummyData || isDeletingAllData || deleteFoodItemMutation.isPending;

  return (
    <div className="container mx-auto px-4 py-8 md:py-12">
      {/* <Button variant="ghost" asChild className="mb-6">
        <Link to="/">
          <span>
            <ArrowLeft className="mr-2 h-4 w-4" /> Kembali
          </span>
        </Link>
      </Button> */}

      <h1 className="text-4xl md:text-5xl font-extrabold mb-8 text-center text-foreground">
        Kelola Data Makanan Supabase
      </h1>

      <div className="mb-8 text-center space-y-4">
        <Button
          onClick={handleSeedDummyData}
          disabled={isAnySeedingOrDeletingActive}
          className="bg-purple-600 hover:bg-purple-700 text-white w-full md:w-auto"
        >
          {isSeedingDummyData ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              <span>Menambahkan Data...</span>
            </>
          ) : (
            <>
              <Package className="mr-2 h-4 w-4" />
              <span>Tambahkan Data Dummy (5 Item)</span>
            </>
          )}
        </Button>
        <p className="text-sm text-muted-foreground">
          Ini akan menambahkan 5 item makanan dummy yang sudah ditentukan sebelumnya ke database Anda.
        </p>

        <Button
          onClick={handleDeleteAllData}
          disabled={isAnySeedingOrDeletingActive}
          variant="destructive"
          className="w-full md:w-auto"
        >
          {isDeletingAllData ? (
            <>
              <Loader2 className="mr-2 h-4 w-4 animate-spin" />
              <span>Menghapus Data...</span>
            </>
          ) : (
            <>
              <Trash2 className="mr-2 h-4 w-4" />
              <span>Hapus Semua Data Makanan</span>
            </>
          )}
        </Button>
        <p className="text-sm text-muted-foreground">
          Ini akan menghapus semua data dari tabel `food_items` di Supabase.
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Add New Food Item Form */}
        <Card className="shadow-lg">
          <CardHeader>
            <CardTitle className="text-2xl font-bold">
              Tambahkan Item Makanan Baru
            </CardTitle>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <Label htmlFor="name">Nama Makanan</Label>
                <Input
                  id="name"
                  name="name"
                  value={newFood.name}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <Label htmlFor="type">Jenis</Label>
                <Input
                  id="type"
                  name="type"
                  value={newFood.type}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <Label htmlFor="origin">Asal</Label>
                <Input
                  id="origin"
                  name="origin"
                  value={newFood.origin}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <Label htmlFor="rating">Rating (0-5)</Label>
                <Input
                  id="rating"
                  name="rating"
                  type="number"
                  step="0.1"
                  min="0"
                  max="5"
                  value={newFood.rating}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <Label htmlFor="imageUrl">URL Gambar</Label>
                <Input
                  id="imageUrl"
                  name="imageUrl"
                  value={newFood.imageUrl}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <Label htmlFor="description">Deskripsi</Label>
                <Textarea
                  id="description"
                  name="description"
                  value={newFood.description}
                  onChange={handleChange}
                  required
                />
              </div>
              <div>
                <Label htmlFor="mostIconic">Paling Ikonik (Opsional)</Label>
                <Input
                  id="mostIconic"
                  name="mostIconic"
                  value={newFood.mostIconic || ""}
                  onChange={handleChange}
                />
              </div>
              <Button type="submit" disabled={addFoodMutation.isPending || isAnySeedingOrDeletingActive}>
                {addFoodMutation.isPending ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    <span>Menambahkan Makanan</span>
                  </>
                ) : (
                  <>
                    <PlusCircle className="mr-2 h-4 w-4" />
                    <span>Tambahkan Makanan</span>
                  </>
                )}
              </Button>
            </form>
          </CardContent>
        </Card>

        {/* Display Existing Food Items */}
        <Card className="shadow-lg">
          <CardHeader>
            <CardTitle className="text-2xl font-bold">
              Daftar Makanan dari Supabase
            </CardTitle>
          </CardHeader>
          <CardContent>
            {isLoading && (
              <div className="flex items-center justify-center p-4">
                <Loader2 className="h-8 w-8 animate-spin text-primary" />
                <span className="ml-3 text-lg text-muted-foreground">
                  Memuat makanan...
                </span>
              </div>
            )}
            {isError && (
              <p className="text-destructive text-center p-4">
                Error: {error?.message || "Gagal memuat data."}
              </p>
            )}
            {foods && foods.length === 0 && !isLoading && (
              <p className="text-muted-foreground text-center p-4">
                Belum ada makanan di Supabase. Tambahkan satu!
              </p>
            )}
            <div className="space-y-4">
              {foods?.map((food) => (
                <div
                  key={food.id}
                  className="flex items-center space-x-4 border-b pb-4 last:border-b-0 last:pb-0"
                >
                  <img
                    src={food.imageUrl}
                    alt={food.name}
                    className="h-16 w-16 object-cover rounded-md flex-shrink-0"
                  />
                  <div className="flex-grow">
                    <h4 className="font-semibold text-lg text-foreground">
                      {food.name}
                    </h4>
                    <p className="text-sm text-muted-foreground">
                      {food.origin} - {food.type}
                    </p>
                    <div className="flex items-center text-yellow-500 text-sm">
                      {Array.from({ length: Math.floor(food.rating) }).map(
                        (_, i) => (
                          <Star key={i} className="h-3 w-3 fill-current" />
                        ),
                      )}
                      <span className="ml-1 text-foreground">
                        {food.rating.toFixed(1)}
                      </span>
                    </div>
                  </div>
                  <Button
                    variant="destructive"
                    size="icon"
                    onClick={() => handleDeleteSingleFood(food.id, food.name)}
                    disabled={deleteFoodItemMutation.isPending || isAnySeedingOrDeletingActive}
                  >
                    {deleteFoodItemMutation.isPending && deleteFoodItemMutation.variables === food.id ? (
                      <Loader2 className="h-4 w-4 animate-spin" />
                    ) : (
                      <Trash2 className="h-4 w-4" />
                    )}
                    <span className="sr-only">Hapus {food.name}</span>
                  </Button>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default SupabaseData;
